import React from 'react';

import { Table } from 'react-bootstrap';
import { Button, ButtonToolbar } from 'react-bootstrap';
import AddDepartmentModal from './AddDepartmentModal';
import EditDepartmentModal from './EditDepartmentModal';

class Department extends React.Component{

    constructor(props) {
        super(props);
        this.state={
            deps:[], 
            showAddDepartmentModal: false,
            showEditDepartmentModal: false
        };

    };

    refreshList() {
        fetch(process.env.REACT_APP_API + 'Department').then(response => response.json()).then(data => {
            this.setState({deps:data});
        });
    };

    componentDidMount() {
        this.refreshList();
    };

    onClickAddDepartment = () => {
        this.setState({ showAddDepartmentModal: true });
    };

    onHideAddDepartmentModal = () => {
        this.setState({ showAddDepartmentModal: false });
        this.refreshList();
    };

    onClickEditDepartment = () => {
        this.setState({
            showEditDepartmentModal: true, 
            departmentId: dep.DepartmentId, 
            departmentName: dep.DepartmentName
        });
    };

    onHideEditDepartmentModal = () => {
        this.setState({ showEditDepartmentModal: false });
        this.refreshList();
    };
    
    render(){
        const {deps, departmentId, departmentName} = this.state;

        return(
            <div>
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>DepartmentId</th>
                            <th>DepartmentName</th>
                            <th>Options</th>
                        </tr>
                    </thead>
                    <tbody>
                        {deps.map(dep => 
                            <tr key={dep.DepartmentId}>
                                <td>{dep.DepartmentId}</td>
                                <td>{dep.DepartmentName}</td>
                                <td>
                                    <ButtonToolbar>
                                        <Button className="mr-2" variant="info" onClick={onClickEditDepartment}> Edit </Button>
                                    </ButtonToolbar>
                                </td>
                            </tr>)}
                    </tbody>
                </Table>

                <ButtonToolbar>
                    <Button variant="primary" onClick={onClickAddDepartment}> Add Department </Button>
                </ButtonToolbar>

                <AddDepartmentModal show={this.state.showAddDepartmentModal} onHide={onHideAddDepartmentModal}/>
                <EditDepartmentModal show={this.state.showEditDepartmentModal} onHide={onHideEditDepartmentModal} departmentId={departmentId} departmentName={departmentName}/>
            </div>
        )
    }
}

export default Department;
